import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom';
import Validacion from './Validacion';
import axios from 'axios';


function Login(){

    const [values, setValues] =useState({
        email:'',
        password:''
    })
    const navigate = useNavigate();
    const [errors,setErrors] =useState({})

    const handleInput = (event) =>{
        setValues(prev => ({...prev, [event.target.name]: [event.target.value]}))
    }

    const handleSubmit =(event) =>{
      //  event.preventDefault();
      //  setErrors(Validacion(values));
       // if(errors.email === "" && errors.password ===""){
            axios.post('http://localhost:8081/', values)
            .then(res => {
                if(res.data === "Correcto"){
                    navigate('/home')
                }else{
                    alert("Usuario no existente");
                }
            })
            .catch(err => console.log(err));  
        //  }
        
    }
    return(
        <div className='d-flex justify-content-center align-items-center bg-primary vh-100'>
            <div className='bg-white p-3 rounded w-25'>
                <form action='' onSubmit={handleSubmit}>
                    <div className='mb-3'>
                        <label htmlFor='email'>Email</label>
                        <input name='email' type='email' placeholder='email' onChange={handleInput} className='form-control rounded-0'></input>
                        {errors.email && <span className='text-danger'> {errors.email}</span>}
                    </div>
                    <div className='mb-3'>
                        <label htmlFor='password'>Password</label>
                        <input name='password' type='password' placeholder='password' onChange={handleInput} className='form-control rounded-0'></input>
                        {errors.password && <span className='text-danger'> {errors.password}</span>}

                    </div>
                    <button type='submit'  className='btn btn-success w-100'>Inicio sesion</button>
                    <p>Holaaaa</p>
                    <Link to="/registrar"  className='btn btn-default border w-100 bg-light text-decoration-none '>Registrarse</Link>

                </form>
            </div>
        </div>
    )
}
export default Login;
