import {React, useState} from 'react'
import { Link, useNavigate} from 'react-router-dom';
import Validacionreg from './Validacionreg';
import axios from 'axios';

function Registrar(){


    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const [values, setValues] =useState({
        name:'',
        email:'',
        password:''
    })
    const navigate = useNavigate();
    const [errors,setErrors] =useState({})

   

    const handleSubmit =(event) =>{
   //     setErrors(Validacionreg(values));
      //  if(errors.name ==="" && errors.email === "" && errors.password ===""){
            axios.post('http://localhost:8081/inicioregistro', {
                name:name,
                email:email,
                password:password
            }).then(res => {
                console.log("resssss")
                console.log(res)
                navigate('/home')
            })
            .catch(err => console.log("Estes el error" + err));  
        //  }
    }

    return(


        <div className='d-flex justify-content-center align-items-center bg-primary vh-100'>
            <div className='bg-white p-3 rounded w-25'>
                <form action='' >
                    <h2>Registro</h2>
                    <div className='mb-3'>
                        <label htmlFor='name'>Nombre</label>
                        <input onChange={(event)=>{
          setName(event.target.value);
        }} name='name' type='text' placeholder='Nombre' className='form-control rounded-0'></input>
                        

                    </div>
                    <div className='mb-3'>
                        <label htmlFor='email'>Email</label>
                        <input 
                        onChange={(event)=>{
                            setEmail(event.target.value);
                          }} name='email' type='email' placeholder='Email' className='form-control rounded-0'></input>
                    

                    </div>
                 
                    <div className='mb-3'>
                        <label htmlFor='password'>Password</label>
                        <input onChange={(event)=>{
          setPassword(event.target.value);
        }} name='password' type='password' placeholder='password' className='form-control rounded-0'></input>

                    </div>
                    <button onClick={handleSubmit} className='btn btn-success w-100'>Registrarse</button>
                    <p>Buenaaasss</p>
                    <Link to="/"  className='btn btn-default border w-100 bg-light text-decoration-none '>Iniciar sesion </Link>

                </form>
            </div>
        </div>
    )
}
export default Registrar;
