import {React, useState} from 'react'
import { Link} from 'react-router-dom';
import Validacionreg from './Validacionreg';
import axios from 'axios';

function Registrar(){

    const [values, setValues] =useState({
        name:'',
        email:'',
        password:''
    })

    const [errors,setErrors] =useState({})

    const handleInput = (event) =>{
        setValues(prev => ({...prev, [event.target.name]: [event.target.value]}))
    }

    const handleSubmit =(event) =>{
        event.preventDefault();
        setErrors(Validacionreg(values));
       
    }

    return(


        <div className='d-flex justify-content-center align-items-center bg-primary vh-100'>
            <div className='bg-white p-3 rounded w-25'>
                <form action='' onSubmit={handleSubmit}>
                    <h2>Registro</h2>
                    <div className='mb-3'>
                        <label htmlFor='name'>Nombre</label>
                        <input onChange={handleInput} name='name' type='text' placeholder='Nombre' className='form-control rounded-0'></input>
                        {errors.name && <span className='text-danger'> {errors.name}</span>}

                    </div>
                    <div className='mb-3'>
                        <label htmlFor='email'>Email</label>
                        <input onChange={handleInput} name='email' type='email' placeholder='Email' className='form-control rounded-0'></input>
                        {errors.email && <span className='text-danger'> {errors.email}</span>}

                    </div>
                    <div className='mb-3'>
                        <label htmlFor='text'>Celular</label>
                        <input onChange={handleInput} name='celular' type='int' placeholder='Celular' className='form-control rounded-0'></input>

                    </div>
                    <div className='mb-3'>
                        <label htmlFor='password'>Password</label>
                        <input onChange={handleInput} name='passwrod' type='password' placeholder='password' className='form-control rounded-0'></input>
                         {errors.password && <span className='text-danger'> {errors.password}</span>}

                    </div>
                    <button  className='btn btn-success w-100'>Registrarse</button>
                    <p>Buenaaasss</p>
                    <Link to="/"  className='btn btn-default border w-100 bg-light text-decoration-none '>Iniciar sesion </Link>

                </form>
            </div>
        </div>
    )
}
export default Registrar;
